import axios from "axios"
import { useState } from "react"
import { Navigate, useNavigate } from "react-router-dom"

function Loginpage(){

  const[FormData,setFormData]=useState({
    username:'',
    password:''
  })

  const[messagecontent,setmessagecontent]=useState()

  const inputData=(event)=>{
    const {name,value}=event.target
    setFormData(prevData=>({
      ...prevData,[name]:value
    }))
  }
  const navigate=useNavigate()
  const SubmitHandle=(event)=>{
    event.preventDefault()
    axios.post('http://localhost:5001/LoginCheck',FormData)
    .then((res)=>{
      if(res.data.status===200){
        setmessagecontent('Login successfully')
        setTimeout(() => {
          navigate('/Dashboard')
        }, 3000);
      }
      setmessagecontent('Check Username and password')
    })
  }

    return(
  <div>
        <center>
          <form onSubmit={SubmitHandle}>
            <input type="text" name="username" value={FormData.username} onChange={inputData} placeholder="Enter the username" />
            <input type="password" name="password" value={FormData.password} onChange={inputData} placeholder="Enter the password" />
            <button>Login</button>
          </form>
          {
            setmessagecontent
          }
        </center>
  </div>      
    )
}
export default Loginpage
