import axios from "axios"
import { useState } from "react"
import '../src/Loginpage.css'
import { Link, useNavigate } from "react-router-dom"
import logo from './Assets/usericon.png'

function Loginpage(){

  const[FormData,setFormData]=useState({
    username:'',
    password:''
  })

  const[messagecontent,setmessagecontent]=useState()

  const inputData=(event)=>{
    const {name,value}=event.target
    setFormData(prevData=>({
      ...prevData,[name]:value
    }))
  }
  const navigate=useNavigate()
  const SubmitHandle=(event)=>{
    event.preventDefault()
    axios.post('http://localhost:5001/LoginCheck',FormData)
    .then((res)=>{
      if(res.data.status===200){
        setmessagecontent('Login successfully')
        setTimeout(() => {
          navigate('/Dashboard')
        }, 3000);
      }
        setmessagecontent('Check Username and password')
    })
  }

    return(
  <div>
        <center className="my-3">
        {
          
        }
        <div className="bg-blue-200 rounded-2xl w-25 flex flex-column  py-1">
        <div className="mb-3">
          <img className="h-50 w-50 " src={logo} alt="userimage" />
          <h5>Sign In</h5>
        </div>
          <form onSubmit={SubmitHandle}>
            <input className=" py-2 w-75 px-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:outline-none" type="text" name="username" value={FormData.username} onChange={inputData} placeholder="Enter the username" /><br /><br />
            <input className=" py-2 w-75 px-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:outline-none" type="password" name="password" value={FormData.password} onChange={inputData} placeholder="Enter the password" /><br /><br />
            <button className=" w-25 bg-blue-500 hover:bg-blue-600 text-white font-semibold px-4 py-2 rounded-md shadow focus:outline-none focus:ring-2 focus:ring-blue-300" >Login</button><br /><br />
          </form>
          <div className="mb-1 flex items-center justify-center">
            <input id="remember" type="checkbox" />
          <label htmlFor="remember" >Remember me</label>
          </div>
          <Link className="mb-2" to=''><span>Forget password?</span></Link>
          <p>Don't have an account?</p>
          <Link className="mb-3" to='/Registrationpage'><span>Register</span></Link>
          
        </div>
        </center>
        <p className="text-center text-lg">About Blog Jobs Help API Privacy Terms Location Erode Contact Uploading & Non-Users Meta Verified </p><p className=" text-lg text-center">English @ 2025 SDLC.</p>
  </div>      
    )
}
export default Loginpage
