import {Link} from 'react-router-dom'
import axios from'axios'
function App(){
  const [userData, setuserData] = useState({
    username: "",
    password: "",
  });

  const FormData = (event) => {
    const { name, value } = event.target;
    setuserData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const SubmitHandle = (event) => {
    event.preventDefault();
    const dataPack = {
      username: FormData.username,
      password: FormData.password,
    };

    axios.post("http://localhost:5001/LoginCheck", dataPack)
    .then((res) => {});
  };
  return(
    <div>
      <center>
        <form action="">
          <div className=" d-flex align-items-center justify-content-center text-center flex-column parent">
      <div className="card px-3 rounded-4 text-center">
        <form className="card-body login " onSubmit={SubmitHandle}>
          {/* {image} */}
          <h4 className="mb-5 mt-2">Sign In</h4>
          <div className="input-group my-3">
            <span className="input-group-text">
              <i className="bx bx-user-circle"></i>
            </span>
            <input
              className=" form-control"
              type="text"
              placeholder="  username"
              name="username"
              value={userData.username}
              onChange={FormData}
            />
          </div>
          <div className="input-group my-3">
            <span className="input-group-text">
              <i className="bx bx-lock-alt"></i>
            </span>
            <input
              className=" form-control"
              type="password"
              placeholder="  password"
              name="password"
              value={userData.password}
              onChange={FormData}
            />
          </div>
          <button className="button my-2 text-white px-4 py-2 rounded w-100">
            <a
              className=" text-decoration-none text-white "
              href="Enquiry.html"
            >
              Login In
            </a>
          </button>
          <div className="mb-4 mt-3 d-flex flex-column justify-content-between">
            <Link to="">
              <p>Forget Password?</p>
            </Link>
          </div>
        </form>
      </div>

      <div>
        <p className="d-flex align-items-center justify-content-center text-center mt-5">
          About Blog Jobs Help API Privacy Terms Location Erode Contact
          Uploading & Non-Users Meta Verified English @ 2025 SDLC.
        </p>
      </div>
    </div>
        </form>
      </center>
    </div>
  )
}
export default App
