import axios from "axios"
import { useState } from "react"
import '../src/Loginpage.css'
import { Link, useNavigate } from "react-router-dom"

function Loginpage(){

  const[FormData,setFormData]=useState({
    username:'',
    password:''
  })

  const[messagecontent,setmessagecontent]=useState()

  const inputData=(event)=>{
    const {name,value}=event.target
    setFormData(prevData=>({
      ...prevData,[name]:value
    }))
  }
  const navigate=useNavigate()
  const SubmitHandle=(event)=>{
    event.preventDefault()
    axios.post('http://localhost:5001/LoginCheck',FormData)
    .then((res)=>{
      if(res.data.status===200){
        setmessagecontent('Login successfully')
        setTimeout(() => {
          navigate('/Dashboard')
        }, 3000);
      }
      else{
        setmessagecontent('Check Username and password')
      }
    })
  }

    return(
  <div>
        <center className="bg-blue-300  w-50">
        <div className="flex flex items-center justify-center h-screen">
          <h5>Sign In</h5>
          <form onSubmit={SubmitHandle}>
            <input className=" py-2 px-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:outline-none" type="text" name="username" value={FormData.username} onChange={inputData} placeholder="Enter the username" /><br /><br />
            <input className=" py-2 px-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:outline-none" type="password" name="password" value={FormData.password} onChange={inputData} placeholder="Enter the password" /><br /><br />
            <button className=" w-25 bg-blue-500 hover:bg-blue-600 text-white font-semibold px-4 py-2 rounded-md shadow focus:outline-none focus:ring-2 focus:ring-blue-300" >Login</button><br /><br />
          </form>
          <input id="remember" type="checkbox" />
          <label htmlFor="remember" >Remember me</label><br />
          <Link to=''><span>Forget password?</span></Link>
          <p>Don't have an account?</p>
          <Link to='/Registrationpage'><span>Register</span></Link>
          {
            messagecontent && <p>{messagecontent}</p>
          }
        </div>
        </center>
        <p className="text-center">About Blog Jobs Help API Privacy Terms Location Erode Contact Uploading & Non-Users Meta Verified
English @ 2025 SDLC.</p>
  </div>      
    )
}
export default Loginpage
