import axios from "axios"
import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"

function Loginpage(){

  const[FormData,setFormData]=useState({
    username:'',
    password:''
  })

  const[messagecontent,setmessagecontent]=useState()

  const inputData=(event)=>{
    const {name,value}=event.target
    setFormData(prevData=>({
      ...prevData,[name]:value
    }))
  }
  const navigate=useNavigate()
  const SubmitHandle=(event)=>{
    event.preventDefault()
    axios.post('http://localhost:5001/LoginCheck',FormData)
    .then((res)=>{
      if(res.data.status===200){
        setmessagecontent('Login successfully')
        setTimeout(() => {
          navigate('/Dashboard')
        }, 3000);
      }
      else{
        setmessagecontent('Check Username and password')
      }
    })
  }

    return(
  <div>
        <center>
          <form onSubmit={SubmitHandle}>
            <input className="w-50 py-2 px-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:outline-none" type="text" name="username" value={FormData.username} onChange={inputData} placeholder="Enter the username" /><br /><br />
            <input className="w-50 py-2 px-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500 focus:outline-none" type="password" name="password" value={FormData.password} onChange={inputData} placeholder="Enter the password" /><br /><br />
            <button className="" >Login</button><br /><br />
          </form>
          <p>Don't have an account?</p>
          <Link to='/Registrationpage'><p>Register</p></Link>
          {
            messagecontent && <p>{messagecontent}</p>
          }
        </center>
  </div>      
    )
}
export default Loginpage
